setting.default-value:
hp-premium-plus-photo-paper if HPColorMatchingIsACM in {true} or
		(HPPhysicalPaperSize has attribute "default-media-type[.='hp-premium-plus-photo-paper']") or
		((not HPPhysicalPaperSize has attribute "default-media-type") and 
		(Printer has attribute "capabilities/media-type-pick/@default[.='hp-premium-plus-photo-paper']"));
* if (HPPhysicalPaperSize has attribute "default-media-type[.='%*%']") or
		((not HPPhysicalPaperSize has attribute "default-media-type") and 
		 (Printer has attribute "capabilities/media-type-pick/@default[.='%*%']"));
plain-paper otherwise;

setting.valid-values:
high-quality: not Printer has attribute "capabilities/grayscale-mode-pick/grayscale-mode[@name='black-only']" or
					not HPPenConfig in {black};
black-only: not HPPenConfig in {colorCMY} and
				HPPaperTypeSupportsBlackInk in {true} and not (HPPaperType in {plain-paper} and HPPrintQuality in {best});
photocopy-fax: (not Printer has attribute "capabilities/grayscale-mode-pick/grayscale-mode[@name='black-only']" or
					not HPPenConfig in {black}) and HPPhysicalPaperSizeIsBorderless in {false};
